INSERT INTO `movies` (`id`, `title`, `duration`, `description`, `country`, `language`, `subtitle`, `ageRating`, `releaseDate`, `endDate`, `bannerUrl`, `posterUrl`, `status`)
VALUES
(1, 'Avengers', 181, 'Những anh hùng hùng mạnh nhất của Trái Đất: Iron Man, Captain America, Thor, Hulk, Black Widow và Hawkeye, phải cùng nhau chống lại kẻ thù nguy hiểm nhất - Thanos, kẻ muốn phá hủy vũ trụ bằng Găng Tay Vô Cực.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'PG-13', '2019-04-26', '2019-07-01', 'img/img/avenger_bg.jpg', 'img/img/avengers.jpg', 'active'),
(2, 'Collage Romance', 90, 'Câu chuyện xoay quanh nhóm sinh viên tại một trường đại học ở Ấn Độ, nơi họ học cách cân bằng giữa tình yêu, tình bạn và áp lực học tập để tạo ra những kỷ niệm đẹp của tuổi trẻ.', 'Ấn Độ', 'Tiếng Hindi', 'Tiếng Anh', 'PG-13', '2023-08-10', '2023-09-30', 'img/img/bg.png', 'img/img/collage_romance.jpg', 'active'),
(3, 'Eternals', 157, 'Những Eternal, các sinh vật bất tử do Celestial tạo ra, đã bí mật sống trên Trái Đất qua hàng ngàn năm để bảo vệ nhân loại khỏi kẻ thù truyền kiếp, những Deviant. Tuy nhiên, bí mật về nguồn gốc của họ được hé lộ, đưa họ vào trận chiến định mệnh.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'PG-13', '2021-11-05', '2022-01-20', 'img/img/bg.png', 'img/img/eternals.jpg', 'active'),
(4, 'Gadar 2', 170, 'Trong phần tiếp theo, Tara Singh lên đường vào giữa lòng cuộc chiến căng thẳng để cứu con trai mình, Charlie. Một câu chuyện kịch tính về tình cha con, lòng trung thành và lòng dũng cảm giữa những sóng gió của thời chiến.', 'Ấn Độ', 'Tiếng Hindi', 'Tiếng Anh', 'PG-13', '2023-08-11', '2023-12-01', 'img/img/gadar_bg.jpg', 'img/img/Gadar2.jpg', 'active'),
(5, 'Jawan', 165, 'Vikram Rathore, một chiến binh bí ẩn, dẫn dắt đội ngũ của mình để chống lại bọn tội phạm và thế lực tham nhũng. Bộ phim là sự kết hợp giữa hành động đỉnh cao và câu chuyện xúc động về hy sinh và tình yêu.', 'Ấn Độ', 'Tiếng Hindi', 'Tiếng Anh', 'PG-13', '2023-09-07', '2023-12-31', 'img/img/bg.png', 'img/img/jawan.jpg', 'active'),
(6, 'John Wick', 101, 'Sau khi vợ mất và chú chó - món quà cuối cùng của cô, bị giết, John Wick, một sát thủ giải nghệ, quyết định trở lại con đường trả thù. Những pha hành động mãn nhãn cùng tâm lý phức tạp của nhân vật chính khiến bộ phim trở thành huyền thoại.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'R', '2014-10-24', '2015-01-15', 'img/img/bg.png', 'img/img/Jhon_Wick.jpg', 'active'),
(7, 'Jurassic World', 124, 'Khi công viên khủng long đầy tham vọng bị mất kiểm soát, các loài khủng long thoát ra ngoài, dẫn đến sự hỗn loạn và nguy hiểm chết người. Bộ phim mang đến hành trình kịch tính giữa con người và sinh vật thời tiền sử.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'PG-13', '2015-06-12', '2015-08-30', 'img/img/bg.png', 'img/img/jurassic_world.jpg', 'active'),
(8, 'Kota Factory', 110, 'Câu chuyện chân thực và cảm động về cuộc sống của các sinh viên tại Kota - trung tâm đào tạo nổi tiếng của Ấn Độ. Bộ phim khám phá áp lực học tập, mối quan hệ và hành trình trưởng thành của mỗi cá nhân.', 'Ấn Độ', 'Tiếng Hindi', 'Tiếng Anh', 'PG-13', '2019-04-15', '2019-12-31', 'img/img/bg.png', 'img/img/kota_factory.jpg', 'active'),
(9, 'Money Heist', 60, 'Dưới sự chỉ đạo của ''Giáo Sư'', một nhóm tội phạm tài năng lên kế hoạch thực hiện phi vụ trộm ngân hàng lớn nhất trong lịch sử. Bộ phim lôi cuốn với những chiến lược thông minh và cảm xúc đa chiều của nhân vật.', 'Tây Ban Nha', 'Tiếng Tây Ban Nha', 'Tiếng Anh', 'TV-MA', '2017-05-02', '2021-12-03', 'img/img/bg.png', 'img/img/money_heist.jpg', 'active'),
(10, 'Moon Knight', 130, 'Marc Spector là một cựu lính đánh thuê bị cuốn vào cuộc chiến giữa các vị thần Ai Cập, nơi anh biến thành Moon Knight, một người anh hùng với nhiều nhân cách khác nhau. Bộ phim chứa đựng những tình tiết kỳ bí và dấn sâu vào những yếu tố tâm lý phức tạp.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'TV-14', '2022-03-30', '2022-05-04', 'img/img/bg.png', 'img/img/moon_knight.jpg', 'active'),
(11, 'Spiderman', 115, 'Peter Parker, một cậu học sinh bị cắn bởi một con nhện biến đổi gen, trở thành Spiderman với sức mạnh phi thường. Cùng với các đồng đội, anh chiến đấu để bảo vệ New York khỏi những mối nguy hiểm từ kẻ thù đe dọa thành phố.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'PG-13', '2002-05-03', '2002-07-17', 'img/img/bg.png', 'img/img/spiderman.jpg', 'active'),
(12, 'Subedar', 150, 'Một bộ phim về cuộc chiến tranh thế giới thứ hai, với nhân vật chính Subedar, người phải dẫn dắt một đội quân đối mặt với những khó khăn và thử thách khủng khiếp trên chiến trường. Câu chuyện cảm động về lòng dũng cảm và sự hy sinh.', 'Ấn Độ', 'Tiếng Hindi', 'Tiếng Anh', 'PG-13', '2022-06-21', '2022-08-15', 'img/img/bg.png', 'img/img/subedar.jpg', 'active'),
(13, 'The Boys', 60, 'Bộ phim xoay quanh những siêu anh hùng bị tha hóa bởi quyền lực, và một nhóm thanh niên, The Boys, quyết tâm vạch trần sự thật về các anh hùng này. Câu chuyện chứa đựng những pha hành động gay cấn và một góc nhìn không khoan nhượng về sự thao túng quyền lực.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'TV-MA', '2019-07-26', '2019-09-30', 'img/img/bg.png', 'img/img/the_boys.jpg', 'active'),
(14, 'Thor: Love and Thunder', 119, 'Thor và các chiến hữu của anh tham gia vào một cuộc hành trình xuyên vũ trụ để đối đầu với Gorr, Kẻ Diệt Các Vị Thần. Bộ phim khám phá những khía cạnh mới của tình bạn và lòng dũng cảm trong cuộc chiến chống lại thần chết.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'PG-13', '2022-07-08', '2022-09-06', 'img/img/bg.png', 'img/img/thor_love_of_thunder.jpg', 'active'),
(16, 'Top Gun', 110, 'Maverick, một phi công tài ba, phải đối mặt với những thử thách khi trở lại trường huấn luyện Top Gun. Bộ phim không chỉ là những cảnh hành động mãn nhãn, mà còn là câu chuyện về sự trưởng thành, lòng tự trọng và tình bạn.', 'Mỹ', 'Tiếng Anh', 'Tiếng Việt', 'PG', '1986-05-16', '1986-08-15', 'img/img/bg.png', 'img/img/topgun.jpg', 'active');
